      $(document).ready(function () {
            $('body').restive(options);
      });
      if($.restive.isPC())
  {
      $('body').restive({
          breakpoints: ['560', '960'],
          classes: ['r_mobi r_phone', 'r_mobi r_tablet'],
          force_dip: true
      });
  }
  else if ($.restive.isMobile())
  {
      $('body').restive({
          breakpoints: ['10000'],
          classes: ['nb'],
          turbo_classes: 'is_mobile=r_mobi,is_phone=r_phone,is_tablet=r_tablet,is_landscape=r_landscape',
          force_dip: true
      });
  }
